import React from 'react';
import Navbar from '/Users/yuvrajaggarwal/Desktop/Task/yoga-admission-form/client/src/navbar.js';
import Footer from '/Users/yuvrajaggarwal/Desktop/Task/yoga-admission-form/client/src/footer.js';

const ClassesPage = () => {
  // Define class timings
  const classTimings = [
    { time: '6-7 AM', days: 'Monday - Friday' },
    { time: '7-8 AM', days: 'Monday - Friday' },
    { time: '8-9 AM', days: 'Monday - Friday' },
    { time: '5-6 PM', days: 'Monday - Friday' },
  ];

  // Define yoga benefits
  const yogaBenefits = [
    'Improved flexibility',
    'Enhanced balance',
    'Mental well-being',
    'Stress reduction',
    'Increased strength',
  ];

  // Styles for the component
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: 'powderblue',
      padding: '20px',
    },
    content: {
      marginTop: '2rem',
      width: '100%',
      maxWidth: '800px',
      margin: 'auto',
    },
    section: {
      marginBottom: '30px',
    },
    heading: {
      fontSize: '2rem',
      marginBottom: '1rem',
      color: '#333',
    },
    listItem: {
      fontSize: '1rem',
      color: '#666',
      marginBottom: '0.5rem',
    },
  };

  // JSX for the component
  return (
    <div style={styles.container}>
      <Navbar />

      {/* Main content */}
      <div style={styles.content}>
        {/* Class Timings Section */}
        <div style={styles.section}>
          <h2 style={styles.heading}>Class Timings</h2>
          <ul>
            {classTimings.map((timing, index) => (
              <li key={index} style={styles.listItem}>
                {timing.time} - {timing.days}
              </li>
            ))}
          </ul>
        </div>

        {/* Yoga Benefits Section */}
        <div style={styles.section}>
          <h2 style={styles.heading}>Yoga Benefits</h2>
          <ul>
            {yogaBenefits.map((benefit, index) => (
              <li key={index} style={styles.listItem}>
                {benefit}
              </li>
            ))}
          </ul>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ClassesPage;
