import React, { useState } from 'react';
import Modal from 'react-modal';

const AdmissionForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: 0,
    email: '',
    phone: '',
    selectedBatch: '',
  });

  const [eligibilityMessage, setEligibilityMessage] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('Form Data:', formData); // Log form data

    // Add validation for email and phone
    if (!validateEmail(formData.email)) {
      setEligibilityMessage('Invalid email address.');
      return;
    }

    if (!validatePhone(formData.phone)) {
      setEligibilityMessage('Invalid phone number.');
      return;
    }

    if (formData.age < 18 || formData.age > 65) {
      setEligibilityMessage('You are not eligible. Age must be between 18 and 65.');

      // Automatically close the form modal after 3000 milliseconds (3 seconds)
      setTimeout(() => {
        setIsModalOpen(false);
      }, 3000);

      return;
    }

    try {
      const response = await fetch('http://localhost:3001/submitForm', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();

      // Assuming your API response structure includes a 'success' property
      console.log('Response:', result); // Log the response

      if (result.success) {
        // Payment successful logic
        setEligibilityMessage('Payment successful!');

        // Automatically close the form modal after 3000 milliseconds (3 seconds)
        setTimeout(() => {
          setIsModalOpen(false);
        }, 3000);
      } else {
        // Payment failed logic
        setEligibilityMessage('Payment failed. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Validate email function
  const validateEmail = (email) => {
    // Use a regex pattern for basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  // Validate phone function
  const validatePhone = (phone) => {
    // Use a regex pattern for basic phone number validation
    const phoneRegex = /^[0-9]{10}$/;
    return phoneRegex.test(phone);
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>Yoga Class Admission App</h1>
        <p style={styles.description}>Enroll in our yoga classes for a healthy and mindful lifestyle.</p>
      </header>

      <div style={styles.content}>
        <button onClick={openModal} style={styles.modalButton}>
          Open 3D Form
        </button>

        <Modal
          isOpen={isModalOpen}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Admission Form Modal"
        >
          <div style={styles.modalContainer}>
            <h2 style={styles.modalHeading}>Admission Form</h2>

            {/* Yoga Picture */}
            <img
              src="https://i.imgur.com/xyz123.jpg" // Replace this URL with your actual yoga image URL
              alt="Yoga"
              style={styles.yogaImage}
              onLoad={() => console.log('Image loaded successfully')}
              onError={() => console.error('Error loading image')}
            />

            <form onSubmit={handleSubmit} style={styles.modalForm}>
              <label style={styles.modalLabel}>
                Name:
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  style={styles.modalInput}
                />
              </label>
              <label style={styles.modalLabel}>
                Age:
                <input
                  type="number"
                  name="age"
                  value={formData.age}
                  onChange={handleChange}
                  style={styles.modalInput}
                />
              </label>
              <label style={styles.modalLabel}>
                Email:
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  style={styles.modalInput}
                />
              </label>
              <label style={styles.modalLabel}>
                Phone:
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  style={styles.modalInput}
                />
              </label>
              <label style={styles.modalLabel}>
                Select Batch:
                <select
                  name="selectedBatch"
                  value={formData.selectedBatch}
                  onChange={handleChange}
                  style={styles.modalInput}
                >
                  <option value="">Select Batch</option>
                  <option value="6-7AM">6-7AM</option>
                  <option value="7-8AM">7-8AM</option>
                  <option value="8-9AM">8-9AM</option>
                  <option value="5-6PM">5-6PM</option>
                </select>
              </label>
              {eligibilityMessage && <p style={styles.modalErrorMessage}>{eligibilityMessage}</p>}
              <button type="submit" style={styles.modalSubmitButton}>
                Submit
              </button>
            </form>
          </div>
        </Modal>
      </div>
    </div>
  );
};

